// Importando o React
import React, {Component} from "react";
// Importando os components necessários da lib react-materialize
import { Row, Col, Card, Input, Button } from 'react-materialize';

import axios from 'axios';

class Forms  extends Component {
    constructor(props){
        super(props)
        this.state = {
          type: "",
          date: "",
          Value: ""
        }
    }
    handleChange(e){
        this.setState({
            text: e.target.value,
            date: "",
          Value: ""
        })
      }
      postContent(){
        axios.post("URL", {
          title: this.state.text,
        })
      }

    render() {
        const rFix = "Renda_Fixa";
        const rVariavel = "Renda_Variavel"
        return(
            <Row>
                <form  >
                    <p className="center-align tit">adicione um investimento:</p>
                    <hr className="hrBl"/>
                    <label className=" col s4">
                        Tipo:
                        <select className="browser-default mbForm mtForm">
                            <option value="" disabled selected>Selecione</option>
                            <option value={this.state.type.rFix} onChange={this.handleChange.bind(this)}>Renda Fixa</option>
                            <option value={this.state.type.rVariavel} onChange={this.handleChange.bind(this)}>Renda Variável</option>
                        </select>
                    </label>
                    <label className=" col s4">
                        Valor:
                        <input className='padInput mbForm mtForm' type="number" placeholder="Valor" value={this.state.Value} onChange={this.handleChange.bind(this)}/>
                    </label>
                    <label className=" col s4">
                        Data:
                        <input className='padInput mtForm' type="date" value={this.state.date} onChange={this.handleChange.bind(this)}/>
                    </label>
                    <div className="col m12 l12 s12">
                        <div className="col m5 l5 s4"></div>
                        <div className="col m2 l2 s4">
                            <button className="btnYe" onClick={this.postContent.bind(this)}>ADICIONAR</button>
                        </div>
                        <div className="col m5 l5 s4"></div>
                    </div>
                </form>

            </Row>
        )
    }
};

export default Forms;
