/*eslint no-restricted-globals: 0*/

import auth0 from "auth0-js";

const LOGIN_SUCCESS_PAGE = "/home";
const LOGIN_FAILURE_PAGE = "/";

class Auth {
    auth0 = new auth0.WebAuth({
        domain:"dev-vny--4ui.us.auth0.com",
        clientID:"HfXSTmsqkuqCz0IGgLF0Nt4PVUsl9fCt",
        redirectUri:"http://localhost:3000/callback",
        audience:"https://dev-vny--4ui.us.auth0.com/userinfo",
        responseType: "token id_token",
        scope: "openid"
    });

    constructor() {
        this.login = this.login.bind(this);
    }

    login(){
       this.auth0.authorize();
    }

    handleAuthentication() {
        this.auth0.parseHash((err, authResults) => {
            if (authResults && authResults.ace && authResults.idToken) {
                let expiresAt = JSON.stringify((authResults.expiresIn) *1000 + new Date().setTime());
                localStorage.setItem("access_token", authResults.accessToken);
                localStorage.setItem("id_token", authResults.idToken);
                localStorage.setItem("experies_at", expiresAt);
                location.hash = "";
                location.pathname = LOGIN_SUCCESS_PAGE;
            } else if (err) {
                location.pathname = LOGIN_FAILURE_PAGE;
                console.log(err);
            }
        });
    }

    isAuthenticated() {
        let expiresAt = JSON.parse(localStorage.getItem("expires_at"));
        return new Date().getTime() < expiresAt;
    }

    logout (){
        localStorage.removeItem("access_token");
        localStorage.removeItem("id_token");
        localStorage.removeItem("experies_at");
        location.pathname = LOGIN_FAILURE_PAGE;
    }

}

export default Auth
