// Importando o React
import React, {Component} from "react";
// Importando os components necessários da lib react-materialize
import { Row, Col, Card, Input, Button } from 'react-materialize';

import axios from 'axios';

class Forms  extends Component {
    constructor(props){
        super(props)
        this.state = {
          text: ""
        }
    }
    handleChange(){

    }

    postContent(){
    }

    render() {
        const { type, date, value } = this.state;
        return(
            <Row>
                <form  >
                    <p className="center-align tit">adicione um investimento:</p>
                    <hr className="hrBl"/>
                    <label className=" col s4">
                        Tipo:
                        <select className="browser-default mbForm mtForm">
                            <option value="" disabled selected>Selecione</option>
                            <option value={this.state.text} onChange={this.handleChange.bind(this)}>Renda Fixa</option>
                            <option value={this.state.text} onChange={this.onChange}>Renda Variável</option>
                        </select>
                    </label>
                    <label className=" col s4">
                        Valor:
                        <input className='padInput mbForm mtForm' type="number" placeholder="Valor" name="value" value={value} onChange={this.onChange}/>
                    </label>
                    <label className=" col s4">
                        Data:
                        <input className='padInput mtForm' type="date" name="date" value={date} onChange={this.onChange}/>
                    </label>
                    <div className="col m12 l12 s12">
                        <div className="col m5 l5 s4"></div>
                        <div className="col m2 l2 s4">
                            <button type="submit" className="btnYe">ADICIONAR</button>
                        </div>
                        <div className="col m5 l5 s4"></div>
                    </div>
                </form>

            </Row>
        )
    }
};

export default Forms;
