// Importando o React
import React, {Component} from "react";
// Importando os components necessários da lib react-materialize
import { Row, Col, Card, Input, Button } from 'react-materialize';

import axios from 'axios';

class Forms  extends Component {
    constructor(props) {
        super(props);
        this.state = { investments: [] };
    }
    componentDidMount() {
        axios.get('https://api-fullstackgo.herokuapp.com/api/details')
            .then(res => {
                this.setState({ investments: res.data });
            })
            .catch(function (error) {
                console.log(error);
            })
    }
    dataTable() {
        return this.state.investments.map((data, i) => {
            return <DataTable obj={data} key={i} />;
        });
    }

    render() {
        const rFix = "Renda_Fixa";
        const rVariavel = "Renda_Variavel"
        return(
            <Row>
<form  >
                    <p className="center-align tit">adicione um investimento:</p>
                    <hr className="hrBl"/>
                    <label className=" col s4">
                        Tipo:
                        <input className='padInput mbForm mtForm' type="text" placeholder="TIpo"/>

                    </label>
                    <label className=" col s4">
                        Valor:
                        <input className='padInput mbForm mtForm' type="number" placeholder="Valor"/>
                    </label>
                    <label className=" col s4">
                        Data:
                        <input className='padInput mtForm' type="date" />
                    </label>
                    <div className="col m12 l12 s12">
                        <div className="col m5 l5 s4"></div>
                        <div className="col m2 l2 s4">
                            <button className="btnYe" onClick={this.postContent.bind(this)}>ADICIONAR</button>
                        </div>
                        <div className="col m5 l5 s4"></div>
                    </div>
                </form>

            </Row>
        )
    }
};

export default Forms;
