// Importando o React
import React, {Component} from "react";
// Importando os components necessários da lib react-materialize
import { Row, Col, Card, Input, Button } from 'react-materialize';

import axios from 'axios';

class Forms  extends Component {
    constructor(props) {
        super(props)

        this.onChangeType = this.onChangeType.bind(this);
        this.onChangeValue = this.onChangeValue.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            type: '',
            Value: '',
            date: ''
        }
    }

    onChangeType(e) {
        this.setState({ type: e.target.value })
    }

    onChangeValue(e) {
        this.setState({ Value: e.target.value })
    }

    onChangeDate(e) {
        this.setState({ date: e.target.value })
    }

    onSubmit(e) {
        e.preventDefault()

        const userObject = {
            type: this.state.type,
            Value: this.state.Value,
            date: this.state.date
        };

        axios.post('https://api-fullstackgo.herokuapp.com/api/interest', userObject)
            .then((res) => {
                console.log(res.data)
            }).catch((error) => {
                console.log(error)
            });

        this.setState({ type: '', Value: '', date: '' })
    }




    render() {
        const rFix = "Renda_Fixa";
        const rVariavel = "Renda_Variavel"
        return(
            <Row>
                <form  onSubmit={this.onSubmit}>
                    <p className="center-align tit">adicione um investimento:</p>
                    <hr className="hrBl"/>
                    <label className=" col s4">
                        Tipo:
                        <input className='padInput mbForm mtForm' type="text" placeholder="Tipo" value={this.state.type} onChange={this.onChangeType}/>

                    </label>
                    <label className=" col s4">
                        Valor:
                        <input className='padInput mbForm mtForm' type="number" placeholder="Valor" value={this.state.Value} onChange={this.onChangeValue}/>
                    </label>
                    <label className=" col s4">
                        Data:
                        <input className='padInput mtForm' type="date" value={this.state.date} onChange={this.onChangeType}/>
                    </label>
                    <div className="col m12 l12 s12">
                        <div className="col m5 l5 s4"></div>
                        <div className="col m2 l2 s4">
                            <button className="btnYe" onClick={this.postContent.bind(this)}>ADICIONAR</button>
                        </div>
                        <div className="col m5 l5 s4"></div>
                    </div>
                </form>

            </Row>
        )
    }
};

export default Forms;
