// Importando o React
import React, {Component} from "react";
// Importando os components necessários da lib react-materialize
import { Row, Col, Card, Input, Button } from 'react-materialize';

import axios from 'axios';

class Forms  extends Component {
    constructor() {
        super();
        this.state = {
            type: '',
            date: '',
            value: '',
        };
    }
    onChange = (e) => {
        this.setState({ [e.target.type]: e.target.value });
    }
    onSubmit = (e) => {
        e.preventDefault();
        // get our form data out of state
        const { type, date, value } = this.state;

        axios.post('https://api-fullstackgo.herokuapp.com/api/details', { type, date, value })
          .then((result) => {
            console.log(result);
            console.log(result.data);
            //access the results here....
          });
      }
    render() {
        const { type, date, value } = this.state;
        return(
            <Row>
                <form  >
                    <p className="center-align tit">adicione um investimento:</p>
                    <hr className="hrBl"/>
                    <label className=" col s4">
                        Tipo:
                        <select className="browser-default mbForm mtForm" name>
                            <option value="" disabled selected>Selecione</option>
                            <option value="1">Renda Fixa</option>
                            <option value="2">Renda Variável</option>
                        </select>
                    </label>
                    <label className=" col s4">
                        Valor:
                        <input className='padInput mbForm mtForm' type="number" placeholder="Valor" name="value"/>
                    </label>
                    <label className=" col s4">
                        Data:
                        <input className='padInput mtForm' type="date" placeholder="Valor" name="date"/>
                    </label>
                    <div className="col m12 l12 s12">
                        <div className="col m5 l5 s4"></div>
                        <div className="col m2 l2 s4">
                            <button type="submit" className="btnYe">ADICIONAR</button>
                        </div>
                        <div className="col m5 l5 s4"></div>
                    </div>
                </form>

            </Row>
        )
    }
};

export default Forms;
