//Importando o React
import React, { Component } from 'react';
import { Button, Modal} from 'react-materialize';
import axios from 'axios'

const API_URL = 'https://api-fullstackgo.herokuapp.com/api';

class Add extends Component {
    state = {
        type: "",
        date: "",
        value: ""
    };
    onTypeChange = e => {
        this.setState({
            type: e.target.value
        });
      };

    onDateChange = e => {
        this.setState({
            date: e.target.value
        });
    };
    onValueChange = e => {
        this.setState({
            value: e.target.value
        });
    };
    handleSubmit = e => {
    e.preventDefault();
    const data = {
        type: this.state.type,
        date: this.state.date,
        value: this.state.value
    };
    axios
        .post("https://jsonplaceholder.typicode.com/posts", data)
        .then(res => console.log(res))
        .catch(err => console.log(err));
    };

    render() {
        return (
            <form className="post">
                    <p className="center-align tit">adicione um investimento:</p>
                    <label>
                        Tipo:
                        <select className="browser-default mbForm mtForm" value={this.state.type} onChange={this.onTypeChange} required>
                            <option value="" disabled selected>Selecione</option>
                            <option value="1">Renda Fixa</option>
                            <option value="2">Renda Vari√°vel</option>
                        </select>
                    </label>
                    <label>
                        Valor:
                        <input className='padInput mbForm mtForm' type="text" placeholder="Valor" name="name" value={this.state.value} onChange={this.onValueChange} required/>
                    </label>
                    <label>
                        Data:
                        <input className='padInput mtForm' type="date" placeholder="Valor" name="name" value={this.state.date} onChange={this.onDateChange} required/>
                    </label>
                    <button type="submit">Create Post</button>
                </form>
        )
    }
};

export default Add;
