import auth0 from "auth0-js";

class Auth {
    auth0 = new auth0.WebAuth({
        domain:"dev-vny--4ui.us.auth0.com",
        clientID:"HfXSTmsqkuqCz0IGgLF0Nt4PVUsl9fCt",
        redirectUri:"http://localhost:3000/callback",
        audience:"https://dev-vny--4ui.us.auth0.com/userinfo",
        responseType: "token id_token",
        scope: "openid"
    });

    constructor() {
        this.login = this.login.bind(this);
    }

    login(){
       this.auth0.authorize();

    }

    handleAuthentication () {
        this.auth0.parseHash((err, authResults) => {
            if (authResults && authResults.accessToken && authResults.idToken) {
                let expriresAt = JSON.stringify((authResults.expiresIn) *1000 + new Date().getTime());
                localStorage.setItem("access_token", authResults.accessToken);
                localStorage.setItem("id_", authResults.accessToken);

            }
        })
    }

}

export default Auth
